// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./interfaces/IFactory.sol";
import "./interfaces/IBondingCurve.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";

/**
 * @title Factory
 * @notice Factory contract for deploying bonding curve systems
 * @dev Implements minimal proxy pattern for gas-efficient deployment
 */
contract Factory is
    IFactory,
    Initializable,
    Ownable2StepUpgradeable,
    ReentrancyGuardUpgradeable
{
    using Clones for address;

    // State variables
    address private tokenImplementation;
    address private bondingCurveImplementation;
    address private lockContract;
    uint256 private deploymentFee;
    BondingCurveSettings private settings;
    // Deployment tracking
    mapping(address => address) private tokenToBondingCurve;
    mapping(address => address) private bondingCurveToToken;

    /**
     * @dev Constructor is disabled since this contract is meant to be used behind a proxy
     */
    constructor() {
        // this line is commented by Menaka to avoid some proxy related conflicts
        //_disableInitializers();
    }

    /**
     * @inheritdoc IFactory
     */
    function initialize(
        uint256 factoryFees,
        address owner,
        address tokenImpl,
        address bondingCurveImpl,
        address lockContractAddr,
        BondingCurveSettings calldata initialSettings
    ) external initializer {
        if (owner == address(0)) revert ZeroAddress();
        if (tokenImpl == address(0)) revert ZeroAddress();
        if (bondingCurveImpl == address(0)) revert ZeroAddress();
        if (lockContractAddr == address(0)) revert ZeroAddress();

        __Ownable_init(owner);
        __ReentrancyGuard_init();

        tokenImplementation = tokenImpl;
        bondingCurveImplementation = bondingCurveImpl;
        lockContract = lockContractAddr;
        settings = initialSettings;
        settings.preBondingTarget = (initialSettings.virtualEth * 20) / 100;
        if (settings.bondingTarget <= settings.preBondingTarget)
            revert InvalidDeploymentParameters();
        deploymentFee = factoryFees;
    }

    function updateBondingCurveSettings(
        BondingCurveSettings calldata newSettings
    ) external onlyOwner {
        // Update settings
        settings = newSettings;
        settings.preBondingTarget = (newSettings.virtualEth * 20) / 100;
        if (settings.bondingTarget <= settings.preBondingTarget)
            revert InvalidDeploymentParameters();
        emit BondingCurveSettingsUpdated(
            newSettings.virtualEth,
            newSettings.preBondingTarget,
            newSettings.bondingTarget,
            newSettings.minContribution,
            newSettings.poolFee
        );
    }

    function getBondingCurveSettings()
        external
        view
        returns (BondingCurveSettings memory)
    {
        return settings;
    }

    /**
     * @inheritdoc IFactory
     */
    function deployBondingCurveSystem(
        string calldata name,
        string calldata symbol
    )
        external
        payable
        nonReentrant
        returns (address tokenAddress, address bondingCurveAddress)
    {
        // Validate deployment fee
        if (msg.value < deploymentFee) {
            revert InsufficientDeploymentFee(msg.value, deploymentFee);
        }

        // Validate parameters
        if (bytes(name).length == 0 || bytes(symbol).length == 0) {
            revert InvalidDeploymentParameters();
        }

        // Deploy token contract
        tokenAddress = tokenImplementation.clone();

        // Initialize token with  _msgSender() as owner
        (bool tokenSuccess, ) = tokenAddress.call(
            abi.encodeWithSignature("initialize(string,string)", name, symbol)
        );
        if (!tokenSuccess) revert InitializationFailed();

        // Deploy bonding curve contract
        bondingCurveAddress = bondingCurveImplementation.clone();

        // Initialize bonding curve
        IBondingCurve(bondingCurveAddress).initialize(
            tokenAddress,
            lockContract,
            _msgSender(),
            settings
        );

        // Mint total supply to the bonding curve contract
        (bool mintSuccess, ) = tokenAddress.call(
            abi.encodeWithSignature(
                "mintTotalSupply(address)",
                bondingCurveAddress
            )
        );
        if (!mintSuccess) revert TokenMintingFailed();

        // Store the relationship between token and bonding curve
        tokenToBondingCurve[tokenAddress] = bondingCurveAddress;
        bondingCurveToToken[bondingCurveAddress] = tokenAddress;

        emit BondingCurveSystemDeployed(
            bondingCurveAddress,
            tokenAddress,
            _msgSender(),
            name,
            symbol
        );

        // Refund excess deployment fee
        if (msg.value > deploymentFee) {
            (bool refundSuccess, ) = _msgSender().call{
                value: msg.value - deploymentFee
            }("");
            if (!refundSuccess) revert RefundFailed();
        }

        return (tokenAddress, bondingCurveAddress);
    }

    /**
     * @inheritdoc IFactory
     */
    function updateDeploymentFee(uint256 newFee) external onlyOwner {
        uint256 oldFee = deploymentFee;
        deploymentFee = newFee;
        emit DeploymentFeeUpdated(oldFee, newFee);
    }

    /**
     * @inheritdoc IFactory
     */
    function withdrawFees(address recipient) external onlyOwner {
        if (recipient == address(0)) revert ZeroAddress();

        uint256 balance = address(this).balance;
        if (balance == 0) revert NoFeesToWithdraw();

        (bool success, ) = recipient.call{value: balance}("");
        if (!success) revert FeeWithdrawalFailed();

        emit FeesWithdrawn(recipient, balance);
    }

    /**
     * @inheritdoc IFactory
     */
    function getDeploymentFee() external view returns (uint256) {
        return deploymentFee;
    }

    /**
     * @inheritdoc IFactory
     */
    function getTokenImplementation() external view returns (address) {
        return tokenImplementation;
    }

    /**
     * @inheritdoc IFactory
     */
    function getBondingCurveImplementation() external view returns (address) {
        return bondingCurveImplementation;
    }

    /**
     * @inheritdoc IFactory
     */
    function getLockContract() external view returns (address) {
        return lockContract;
    }

    /**
     * @notice Get the bonding curve address associated with a token
     * @param token The token address to query
     * @return The associated bonding curve address
     */
    function getBondingCurveForToken(
        address token
    ) external view returns (address) {
        return tokenToBondingCurve[token];
    }

    /**
     * @notice Get the token address associated with a bonding curve
     * @param bondingCurve The bonding curve address to query
     * @return The associated token address
     */
    function getTokenForBondingCurve(
        address bondingCurve
    ) external view returns (address) {
        return bondingCurveToToken[bondingCurve];
    }

    /**
     * @dev Required for the contract to receive ETH
     */
    receive() external payable {}
}

contracts/Foundry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./interfaces/IFoundry.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title Foundry
 * @notice Main deployer contract for the bonding curve system
 * @dev Implements minimal proxy pattern for gas-efficient deployment
 */
contract Foundry is IFoundry, Ownable, ReentrancyGuard, Pausable {
    using Clones for address;

    // Implementation addresses
    address private factoryImplementation;
    address private lockImplementation;
    address private tokenImplementation;
    address private bondingCurveImplementation;

    // Deployment fee
    uint256 private deploymentFee;

    // Mapping to track deployed clones
    mapping(address => bool) private deployedClones;

    /**
     * @notice Constructor to initialize the Foundry contract
     */
    constructor(
        address _factoryImpl,
        address _lockImpl,
        address _tokenImpl,
        address _bondingCurveImpl,
        uint256 _initialFee
    ) Ownable(msg.sender) {
        if (_factoryImpl == address(0)) revert ZeroAddress();
        if (_lockImpl == address(0)) revert ZeroAddress();
        if (_tokenImpl == address(0)) revert ZeroAddress();
        if (_bondingCurveImpl == address(0)) revert ZeroAddress();

        factoryImplementation = _factoryImpl;
        lockImplementation = _lockImpl;
        tokenImplementation = _tokenImpl;
        bondingCurveImplementation = _bondingCurveImpl;
        deploymentFee = _initialFee;
    }

    // Implementation of view functions
    function getFactoryImplementation() external view returns (address) {
        return factoryImplementation;
    }

    function getLockImplementation() external view returns (address) {
        return lockImplementation;
    }

    function getTokenImplementation() external view returns (address) {
        return tokenImplementation;
    }

    function getBondingCurveImplementation() external view returns (address) {
        return bondingCurveImplementation;
    }

    function getDeploymentFee() external view returns (uint256) {
        return deploymentFee;
    }

    function isDeployedClone(
        address cloneAddress
    ) external view returns (bool) {
        return deployedClones[cloneAddress];
    }

    /**
     * @inheritdoc IFoundry
     */
    function deploySystem(
        address owner,
        uint256 factoryFees,
        IFactory.BondingCurveSettings calldata settings
    )
        external
        payable
        whenNotPaused
        nonReentrant
        returns (address factoryAddress, address lockAddress)
    {
        if (msg.value < deploymentFee) {
            revert InsufficientDeploymentFee(msg.value, deploymentFee);
        }
        if (owner == address(0)) revert ZeroAddress();

        // Deploy lock contract clone
        lockAddress = lockImplementation.clone();
        deployedClones[lockAddress] = true;

        // Initialize lock contract
        (bool lockSuccess, ) = lockAddress.call(
            abi.encodeWithSignature("initialize()")
        );
        if (!lockSuccess) revert InitializationFailed("lock");

        // Deploy factory clone
        factoryAddress = factoryImplementation.clone();
        deployedClones[factoryAddress] = true;

        // Initialize factory with all required parameters
        IFactory(factoryAddress).initialize(
            factoryFees,
            owner,
            tokenImplementation,
            bondingCurveImplementation,
            lockAddress,
            settings
        );

        emit SystemDeployed(factoryAddress, lockAddress, owner);

        // Refund excess fee if any
        if (msg.value > deploymentFee) {
            (bool refundSuccess, ) = msg.sender.call{
                value: msg.value - deploymentFee
            }("");
            if (!refundSuccess) revert FeeOperationFailed("refund");
        }

        return (factoryAddress, lockAddress);
    }

    /**
     * @inheritdoc IFoundry
     */
    function updateImplementation(
        string calldata implementationType,
        address newImplementation
    ) external onlyOwner {
        if (newImplementation == address(0)) revert ZeroAddress();

        address oldImplementation;
        bytes32 implType = keccak256(bytes(implementationType));

        if (implType == keccak256(bytes("factory"))) {
            oldImplementation = factoryImplementation;
            factoryImplementation = newImplementation;
        } else if (implType == keccak256(bytes("lock"))) {
            oldImplementation = lockImplementation;
            lockImplementation = newImplementation;
        } else if (implType == keccak256(bytes("token"))) {
            oldImplementation = tokenImplementation;
            tokenImplementation = newImplementation;
        } else if (implType == keccak256(bytes("bondingCurve"))) {
            oldImplementation = bondingCurveImplementation;
            bondingCurveImplementation = newImplementation;
        } else {
            revert InvalidImplementationType(implementationType);
        }

        emit ImplementationUpdated(
            implementationType,
            oldImplementation,
            newImplementation
        );
    }

    /**
     * @inheritdoc IFoundry
     */
    function updateDeploymentFee(uint256 newFee) external onlyOwner {
        uint256 oldFee = deploymentFee;
        deploymentFee = newFee;
        emit DeploymentFeeUpdated(oldFee, newFee);
    }

    /**
     * @inheritdoc IFoundry
     */
    function withdrawFees(address recipient) external onlyOwner {
        if (recipient == address(0)) revert ZeroAddress();
        uint256 balance = address(this).balance;
        if (balance == 0) revert NoFeesToWithdraw();

        (bool success, ) = recipient.call{value: balance}("");
        if (!success) revert FeeOperationFailed("withdrawal");

        emit FeesWithdrawn(recipient, balance);
    }

    /**
     * @notice Pause the contract
     * @dev Only callable by owner
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract
     * @dev Only callable by owner
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Required for the contract to receive ETH
     */
    receive() external payable {}
}

contracts/interfaces/IBondingCurve.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "./IFactory.sol";

/**
 * @title IBondingCurve
 * @notice Interface for the bonding curve contract that manages token distribution and liquidity
 * @dev Implements constant product AMM formula with pre-bonding and bonding phases
 */
interface IBondingCurve {
    // Custom Errors
    error ZeroAddress();
    error InitializationFailed();
    error PreBondingTargetReached();
    error BondingTargetReached();
    error ContributionTooLow();
    error InvalidPhase();
    error TokensLocked();
    error TokensNotLocked();
    error InsufficientTokens();
    error InsufficientETH();
    error SlippageExceeded();
    error TransferFailed();
    error CannotFinalizeYet();
    error AlreadyFinalized();
    error NoFeesToWithdraw();

    // Events
    event PreBondingContribution(
        address indexed contributor,
        uint256 amount,
        uint256 tokenAmount
    );

    event PreBondingCompleted(uint256 totalContributed, uint256 totalTokens);

    event TokensUnlocked(address indexed account);

    event TokensPurchased(
        address indexed buyer,
        uint256 ethAmount,
        uint256 tokenAmount
    );

    event TokensSold(
        address indexed seller,
        uint256 tokenAmount,
        uint256 ethAmount,
        uint256 fee
    );

    event CurveFinalized(
        address indexed uniswapPool,
        uint256 indexed lpTokenId
    );

    event FeesWithdrawn(uint256 amount);

    // State-Changing Functions
    /**
     * @notice Initialize the bonding curve contract
     * @param token_ Address of the ERC20 token
     * @param lock_ Address of the LP NFT lock contract
     * @param owner_ Address that will own the contract
     */
    // Update initialize function
    function initialize(
        address token_,
        address lock_,
        address owner_,
        IFactory.BondingCurveSettings calldata settings_
    ) external;

    /**
     * @notice Contribute ETH during pre-bonding phase
     */
    function contributePreBonding() external payable;

    /**
     * @notice Buy tokens during bonding phase
     * @param minTokens Minimum tokens to receive (slippage protection)
     */
    function buyTokens(
        uint256 minTokens
    ) external payable returns (uint256 tokensToReceive);

    /**
     * @notice Sell tokens during bonding phase
     * @param tokenAmount Amount of tokens to sell
     * @param minETH Minimum ETH to receive (slippage protection)
     */
    function sellTokens(
        uint256 tokenAmount,
        uint256 minETH
    ) external returns (uint256 ethToReceive, uint256 fee);

    /**
     * @notice Finalize the curve and create Uniswap pool
     * @dev Only callable when bonding target is reached
     */
    function finalizeCurve() external;
}

contracts/interfaces/IFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IFactory
 * @notice Interface for the factory contract that deploys token and bonding curve instances
 * @dev Factory is deployed via minimal proxy pattern and implements Initializable
 */
interface IFactory {
    // Custom Errors
    /// @notice Thrown when an address parameter is zero
    error ZeroAddress();

    /// @notice Thrown when initialization fails
    error InitializationFailed();

    /// @notice Thrown when caller is not authorized
    error Unauthorized();

    /// @notice Thrown when deployment fee is insufficient
    /// @param provided The amount of ETH provided
    /// @param required The required deployment fee
    error InsufficientDeploymentFee(uint256 provided, uint256 required);

    /// @notice Thrown when deployment parameters are invalid
    error InvalidDeploymentParameters();

    /// @notice Thrown when deployment minting fails
    error TokenMintingFailed();

    /// @notice Thrown when deployment extra fees refund fails
    error RefundFailed();
    error NoFeesToWithdraw();
    error FeeWithdrawalFailed();

    // Events
    /**
     * @notice Emitted when a new bonding curve system is deployed
     * @param bondingCurveAddress The address of the deployed bonding curve
     * @param tokenAddress The address of the deployed token
     * @param owner The address that will own both contracts
     * @param name Token name
     * @param symbol Token symbol
     */
    event BondingCurveSystemDeployed(
        address indexed bondingCurveAddress,
        address indexed tokenAddress,
        address indexed owner,
        string name,
        string symbol
    );

    /**
     * @notice Emitted when deployment fee is updated
     * @param oldFee Previous fee amount
     * @param newFee New fee amount
     */
    event DeploymentFeeUpdated(uint256 oldFee, uint256 newFee);

    /**
     * @notice Emitted when fees are withdrawn
     * @param recipient Address receiving the fees
     * @param amount Amount withdrawn
     */
    event FeesWithdrawn(address indexed recipient, uint256 amount);

    // Add new events
    event BondingCurveSettingsUpdated(
        uint256 virtualEth,
        uint256 preBondingTarget,
        uint256 bondingTarget,
        uint256 minContribution,
        uint24 poolFee
    );

    struct BondingCurveSettings {
        uint256 virtualEth;
        uint256 preBondingTarget;
        uint256 bondingTarget;
        uint256 minContribution;
        uint24 poolFee;
        uint24 sellFee;
        address uniswapV3Factory;
        address positionManager;
        address weth;
        address feeTo;
    }

    // Add new functions
    function getBondingCurveSettings()
        external
        view
        returns (BondingCurveSettings memory);

    function updateBondingCurveSettings(
        BondingCurveSettings calldata newSettings
    ) external;

    // View Functions
    /**
     * @notice Get the current deployment fee
     * @return Current fee amount in wei
     */
    function getDeploymentFee() external view returns (uint256);

    /**
     * @notice Get the token implementation address
     * @return Address of the token implementation contract
     */
    function getTokenImplementation() external view returns (address);

    /**
     * @notice Get the bonding curve implementation address
     * @return Address of the bonding curve implementation contract
     */
    function getBondingCurveImplementation() external view returns (address);

    /**
     * @notice Get the LP NFT lock contract address
     * @return Address of the LP NFT lock contract
     */
    function getLockContract() external view returns (address);

    // State-Changing Functions
    /**
     * @notice Initialize the factory contract
     * @param owner Address that will own the factory
     * @param tokenImpl Address of the token implementation
     * @param bondingCurveImpl Address of the bonding curve implementation
     * @param lockContract Address of the LP NFT lock contract
     */
    // Update initialize function
    function initialize(
        uint256 factoryFees,
        address owner,
        address tokenImpl,
        address bondingCurveImpl,
        address lockContract,
        BondingCurveSettings calldata settings
    ) external;

    /**
     * @notice Deploy a complete bonding curve system (token + bonding curve)
     * @param name Token name
     * @param symbol Token symbol
     * @return tokenAddress Address of the deployed token contract
     * @return bondingCurveAddress Address of the deployed bonding curve
     */
    function deployBondingCurveSystem(
        string calldata name,
        string calldata symbol
    )
        external
        payable
        returns (address tokenAddress, address bondingCurveAddress);

    /**
     * @notice Update the deployment fee
     * @param newFee New fee amount in wei
     */
    function updateDeploymentFee(uint256 newFee) external;

    /**
     * @notice Withdraw collected fees
     * @param recipient Address to receive the fees
     */
    function withdrawFees(address recipient) external;
}

contracts/interfaces/IFoundry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "./IFactory.sol";

/**
 * @title IFoundry
 * @notice Interface for the main deployer contract that manages the bonding curve system
 * @dev Handles deployment and initialization of the entire bonding curve ecosystem
 */
interface IFoundry {
    // Custom Errors
    /// @notice Thrown when an address parameter is zero
    error ZeroAddress();

    /// @notice Thrown when initialization of a contract fails
    /// @param contractType The type of contract that failed to initialize
    error InitializationFailed(string contractType);

    /// @notice Thrown when the deployment fee provided is insufficient
    /// @param provided The amount of ETH provided
    /// @param required The required deployment fee
    error InsufficientDeploymentFee(uint256 provided, uint256 required);

    /// @notice Thrown when an invalid implementation type is provided
    /// @param providedType The invalid implementation type
    error InvalidImplementationType(string providedType);

    /// @notice Thrown when a fee-related operation fails
    /// @param operation The operation that failed (e.g., "refund", "withdrawal")
    error FeeOperationFailed(string operation);

    /// @notice Thrown when attempting to withdraw zero fees or when contract has no balance
    error NoFeesToWithdraw();

    /**
     * @notice Emitted when a new system (factory + lock) is deployed
     * @param factoryAddress The address of the newly deployed factory
     * @param lockAddress The address of the newly deployed lock contract
     * @param owner The address of the factory owner
     */
    event SystemDeployed(
        address indexed factoryAddress,
        address indexed lockAddress,
        address indexed owner
    );

    /**
     * @notice Emitted when implementation addresses are updated
     * @param implementationType The type of implementation being updated
     * @param oldImplementation The previous implementation address
     * @param newImplementation The new implementation address
     */
    event ImplementationUpdated(
        string indexed implementationType,
        address indexed oldImplementation,
        address indexed newImplementation
    );

    /**
     * @notice Emitted when the deployment fee is updated
     * @param oldFee The previous fee amount
     * @param newFee The new fee amount
     */
    event DeploymentFeeUpdated(uint256 oldFee, uint256 newFee);

    /**
     * @notice Emitted when fees are withdrawn
     * @param recipient The address receiving the fees
     * @param amount The amount withdrawn
     */
    event FeesWithdrawn(address indexed recipient, uint256 amount);

    // View Functions
    function getFactoryImplementation() external view returns (address);

    function getLockImplementation() external view returns (address);

    function getTokenImplementation() external view returns (address);

    function getBondingCurveImplementation() external view returns (address);

    function getDeploymentFee() external view returns (uint256);

    function isDeployedClone(address cloneAddress) external view returns (bool);

    /**
     * @notice Deploy both factory and lock contracts in a single transaction
     * @param owner Address that will own the factory
     * @param settings Bonding curve settings for initialization
     * @return factoryAddress The address of the newly deployed factory
     * @return lockAddress The address of the newly deployed lock contract
     */
    function deploySystem(
        address owner,
         uint256 factoryFees,
        IFactory.BondingCurveSettings calldata settings
    ) external payable returns (address factoryAddress, address lockAddress);

    /**
     * @notice Update the implementation address for a specific contract type
     * @param implementationType The type of implementation to update
     * @param newImplementation The new implementation address
     */
    function updateImplementation(
        string calldata implementationType,
        address newImplementation
    ) external;

    /**
     * @notice Update the deployment fee
     * @param newFee The new fee amount in wei
     */
    function updateDeploymentFee(uint256 newFee) external;

    /**
     * @notice Withdraw collected fees
     * @param recipient The address to receive the fees
     */
    function withdrawFees(address recipient) external;
}

contracts/interfaces/ILock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ILock
 * @notice Interface for the LP NFT lock contract
 * @dev Handles locking of Uniswap V3 LP NFTs and fee management
 */
interface ILock {
    // Custom Errors
    /// @notice Thrown when NFT is already locked
    error NFTAlreadyLocked();

    /// @notice Thrown when NFT is not locked
    error NFTNotLocked();

    /// @notice Thrown when caller is not the NFT owner
    error NotNFTOwner();

    /// @notice Thrown when lock period has not ended
    error LockPeriodNotEnded();

    // Events
    /**
     * @notice Emitted when an NFT is locked
     * @param owner Address of the NFT owner
     * @param tokenId ID of the locked NFT
     */
    event NFTLocked(address indexed owner, uint256 indexed tokenId);

    /**
     * @notice Emitted when an NFT is unlocked
     * @param owner Address of the NFT owner
     * @param tokenId ID of the unlocked NFT
     */
    event NFTUnlocked(
        address indexed owner,
        uint256 indexed tokenId,
        address to
    );

    /**
     * @notice Emitted when fees are claimed
     * @param owner Address claiming the fees
     * @param tokenId NFT position ID
     * @param amount0 Amount of token0 fees claimed
     * @param amount1 Amount of token1 fees claimed
     */
    event FeesClaimed(
        address indexed owner,
        uint256 indexed tokenId,
        uint256 amount0,
        uint256 amount1
    );

    // Functions
    function initialize() external;

    function lockNFT(uint256 tokenId, address owner) external;

    function unlockNFT(uint256 tokenId, address to) external;

    function claimFees(
        uint256 tokenId
    ) external returns (uint256 amount0, uint256 amount1);

    function checkAvailableFees(
        uint256 tokenId
    ) external view returns (uint256 amount0, uint256 amount1);

    function getNFTsByOwner(
        address owner
    ) external view returns (uint256[] memory);

    function isNFTLocked(uint256 tokenId) external view returns (bool);

    function getRemainingLockTime(
        uint256 tokenId
    ) external view returns (uint256);
}

