// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../libraries/UniswapPoolCreator.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";

interface IWETH {
    function deposit() external payable;

    function approve(address spender, uint256 amount) external returns (bool);
}

/**
 * @title UniswapPoolCreatorTest
 * @notice Test wrapper contract for UniswapPoolCreator library
 */
contract UniswapPoolCreatorTest is IERC721Receiver {
    using UniswapPoolCreator for *;

    event PoolCreated(address pool);
    event PositionCreated(uint256 tokenId);

    /**
     * @notice Test function for creating and initializing a Uniswap V3 pool
     */
    function testCreateAndInitializePool(
        UniswapPoolCreator.PoolParams calldata params
    ) external returns (address pool) {
        pool = UniswapPoolCreator.createAndInitializePool(params);
        emit PoolCreated(pool);
        return pool;
    }

    /**
     * @notice Test function for creating an LP position
     */
    function testCreateLPPosition(
        UniswapPoolCreator.PositionParams calldata params
    ) external payable returns (uint256 tokenId) {
        // Wrap ETH to WETH
        //IWETH(params.weth).deposit{value: params.ethAmount}();

        // Approve tokens to position manager
        //IWETH(params.weth).approve(params.positionManager, params.ethAmount);
        IERC20(params.token).approve(
            params.positionManager,
            params.tokenAmount
        );

        tokenId = UniswapPoolCreator.createLPPosition(params);
        emit PositionCreated(tokenId);
        return tokenId;
    }

    /**
     * @notice Implementation of IERC721Receiver
     */
    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return this.onERC721Received.selector;
    }

    /**
     * @notice Allow the contract to receive ETH
     */
    receive() external payable {}
}

contracts/TokenImplementation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

/**
 * @title TokenImplementation
 * @notice Implementation of the ERC20 token with one-time mint capability
 * @dev Deployed via minimal proxy pattern with fixed total supply
 */
contract TokenImplementation is Initializable, ERC20Upgradeable {
    uint256 public constant TOTAL_SUPPLY = 1_000_000_000 * 10 ** 18; // 1 billion tokens with 18 decimals
    bool private _minted;
    address public factory;

    /// @notice Thrown when attempting to mint tokens more than once
    error AlreadyMinted();

    /// @notice Thrown when an address parameter is zero
    error ZeroAddress();

    /// @notice Thrown when caller is not the factory
    error OnlyFactory();

    /// @notice Emitted when the initial token supply is minted
    event InitialMintCompleted(address indexed recipient, uint256 amount);

    /**
     * @dev Constructor disabled since this contract is meant to be used behind a proxy
     */
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initialize the token contract
     * @param name_ Token name
     * @param symbol_ Token symbol
     */
    function initialize(
        string memory name_,
        string memory symbol_
    ) external initializer {
        __ERC20_init(name_, symbol_);
        factory = msg.sender;
        _minted = false;
    }

    /**
     * @notice Mint the total supply of tokens to a specified address
     * @dev Can only be called once by the factory
     * @param to Address to receive the minted tokens
     */
    function mintTotalSupply(address to) external {
        if (msg.sender != factory) revert OnlyFactory();
        if (_minted) revert AlreadyMinted();
        if (to == address(0)) revert ZeroAddress();

        _mint(to, TOTAL_SUPPLY);
        _minted = true;

        emit InitialMintCompleted(to, TOTAL_SUPPLY);
    }

    /**
     * @notice Check if the total supply has been minted
     * @return Boolean indicating if tokens have been minted
     */
    function isMinted() external view returns (bool) {
        return _minted;
    }

    /**
     * @notice Override to prevent transfers when tokens haven't been minted
     * @param from The sender's address
     * @param to The recipient's address
     * @param amount The amount to transfer
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        if (!_minted && from != address(0))
            revert("Transfers disabled before mint");
        super._update(from, to, amount);
    }
}
